const fixedHeaderWhenScrolling=()=>{const e=document.querySelector(".header");window.scrollY>0?e.classList.add("header--fixed"):e.classList.remove("header--fixed")},openSidebar=()=>{const e=document.querySelector(".header__burger"),d=document.querySelector(".sidebar__close"),r=document.querySelector(".sidebar");e.addEventListener("click",(()=>{r.classList.add("sidebar--open")})),d.addEventListener("click",(()=>{r.classList.remove("sidebar--open")}))};document.addEventListener("DOMContentLoaded",(()=>{window.addEventListener("scroll",(function(){fixedHeaderWhenScrolling()})),openSidebar()}));